const reviews = items.map(item => item.json);

const processedData = reviews.map(item => {
  const text = item.data || '';
  const stopWords = ['그리고', '싶다', '이다', '하다', '것', '수', '등', '및', '또는'];

  const words = text.split(/\s+/).filter(word => word.trim() !== '');
  const filteredWords = words.filter(word => !stopWords.includes(word));
  const nounsAdjectives = filteredWords.filter(word => isNounOrAdjective(word));

  const processedText = nounsAdjectives.join(' ');

  // QuickChart API용 JSON 구조 생성
  return {
    // 워드클라우드 API용 페이로드
    wordcloudPayload: {
      format: "png",
      width: 700,
      height: 700,
      fontFamily: "sans-serif",
      fontScale: 18,
      scale: "linear",
      text: processedText,
      backgroundColor: "white",
      colorScheme: "paired",
      maxWords: 100
    },
    // 디버깅용 정보
    originalData: item.data,
    processedText: processedText,
    wordCount: nounsAdjectives.length,
    words: nounsAdjectives
  };
});

return processedData.map(item => ({ json: item }));

// 기존 함수들...
function isNounOrAdjective(word) {
  if (word.length < 2) return false;
  const tagged = tagPOS(word);
  return tagged === 'N' || tagged === 'A';
}

function tagPOS(word) {
  const techNouns = ['인공지능', '머신러닝', '딥러닝', '데이터분석', '빅데이터', '알고리즘', 
                   '프로그래밍', '파이썬', '자바', '웹개발', '모바일앱', '클라우드', '컴퓨팅'];

  if (techNouns.includes(word) || word.length >= 3) {
    return 'N';
  }

  return word.length > 2 ? 'N' : 'A';
}
