const reviews = items.map(item => item.json);

const processedData = reviews.map(item => {
  const text = item.data || '';
  const stopWords = ['그리고', '싶다', '이다', '하다', '것', '수', '등', '및', '또는'];

  const words = text.split(/\s+/).filter(word => word.trim() !== '');
  const filteredWords = words.filter(word => !stopWords.includes(word));
  const nounsAdjectives = filteredWords.filter(word => isNounOrAdjective(word));

  const processedText = nounsAdjectives.join(' ');

  // QuickChart API용 JSON 구조 생성
  return {
    // 워드클라우드 API용 페이로드
    wordcloudPayload: {
      format: "png",
      width: 700,
      height: 700,
      fontFamily: "sans-serif",
      fontScale: 18,
      scale: "linear",
      text: processedText,
      backgroundColor: "white",
      colorScheme: "paired",
      maxWords: 100
    },
    // 디버깅용 정보
    originalData: item.data,
    processedText: processedText,
    wordCount: nounsAdjectives.length,
    words: nounsAdjectives
  };
});

return processedData.map(item => ({ json: item }));

// 기존 함수들...
function isNounOrAdjective(word) {
  if (word.length < 2) return false;
  const tagged = tagPOS(word);
  return tagged === 'N' || tagged === 'A';
}

function tagPOS(word) {
  const techNouns = ['인공지능', '머신러닝', '딥러닝', '데이터분석', '빅데이터', '알고리즘', 
                   '프로그래밍', '파이썬', '자바', '웹개발', '모바일앱', '클라우드', '컴퓨팅'];

  if (techNouns.includes(word) || word.length >= 3) {
    return 'N';
  }

  return word.length > 2 ? 'N' : 'A';
}

// Mode: Run Once for All Items
const items = $input.all();

// 모든 item의 binary를 모아 키/파일명 기준으로 탐색
const images = { positive: null, negative: null };

for (const it of items) {
  const bin = it.binary || {};
  for (const [propName, b] of Object.entries(bin)) {
    const fname = (b.fileName || propName || '').toLowerCase();

    if (!images.positive && (fname.includes('positive') || fname.includes('pos'))) {
      images.positive = b.data;        // base64 문자열
    }
    if (!images.negative && (fname.includes('negative') || fname.includes('neg'))) {
      images.negative = b.data;        // base64 문자열
    }
  }
}

// 안전장치: 매칭 못했으면 첫 번째/두 번째 png로 대체
if (!images.positive || !images.negative) {
  const pngs = [];
  for (const it of items) {
    for (const b of Object.values(it.binary || {})) {
      if ((b.mimeType || '').includes('png')) pngs.push(b.data);
    }
  }
  if (!images.positive) images.positive = pngs[0] || '';
  if (!images.negative) images.negative = pngs[1] || pngs[0] || '';
}

return [
  {
    json: {
      html: `
<h2>리뷰 분석 결과</h2>
<p>아래는 긍정/부정 리뷰 워드클라우드입니다:</p>

<h3>긍정 리뷰</h3>
<img src="data:image/png;base64,${images.positive || ''}" style="max-width:100%;height:auto;" />

<h3>부정 리뷰</h3>
<img src="data:image/png;base64,${images.negative || ''}" style="max-width:100%;height:auto;" />

<hr />
<p style="color:#6b7280;font-size:12px;">This email was sent automatically with n8n</p>
      `.trim()
    }
  }
];
